import React, {Component} from 'react';
import '../App.css';
class Item extends Component {
  onUpdateStatus=()=>{
   // console.log(this.props.task.id);
   this.props.onUpdateStatuszz(this.props.key)
  }
  render(){
    var {task,index} = this.props;
    return (
            <tr>
                  <td>{index+1}</td>
                  <td>{task.name}</td>

                  <td className="text-center">{task.price}</td>
                  <td className="text-center"><img alt={task.name} src={task.image}/></td>
                  <td className="text-center">
                  <span className={task.status=== true ?'label label-success':'label label-danger'} onClick={this.onUpdateStatus}>{task.status === true ?'Kich hoat':'An'}</span>
                  </td>
                  <td className="text-center">     
                      <button type="button" className="btn btn-warning">
                          <i className="fa fa-pencil mr-5"></i>
                          Sua
                        </button>&nbsp;
                        <button type="button" className="btn btn-danger">
                          <i className="fa fa-trash mr-5"></i>
                          Xoa
                        </button>
                  </td>
              </tr>

    );
  }

}

export default Item;
