import React, {Component} from 'react';
import '../App.css';
class Form extends Component {
    constructor(props){
        super(props);
        this.state = {
            id:'',
            name:'',
            price:'',
            quantity:'',
            image:'',
            status:false
        }

    }
    UNSAFE_componentWillMount(){//duoc goi 1 lan khi mo form
        //khi form duoc mo kiem tra xem task tuc la taskEditing co gia tri hay ko de phan biet them va sua
        if(this.props.task){
            this.setState({
                id: this.props.task.id,
                name: this.props.task.name,
                price: this.props.task.price,
                quantity: this.props.task.quantity,
                image: this.props.task.image,
                status: this.props.task.status
            });
            //console.log(this.state);
        }
    }
    UNSAFE_componentWillReceiveProps(nextProps){
        if(nextProps && nextProps.task){
            this.setState({
                id: nextProps.task.id,
                name: nextProps.task.name,
                price: nextProps.task.price,
                uantity: nextProps.task.quantity,
                image: nextProps.task.image,
                status: nextProps.task.status
            });
            //console.log(this.state);
        }
        else if(nextProps.task===null)
        {
            this.setState({
                id:'',
                name:'',
                price:'',
                quantity:'',
                image:'',
                status:false
            });
        }
    }
    oncloseForm=()=>{
        this.props.oncloseForms();
    }
    onChange = (event)=>{
        var target=event.target;
        var name = target.name;
        var value = target.value;
        if(name==='status'){
            value = target.value === 'true'? true:false;// chuyen sang kieu boolean
        }
        this.setState({
            [name]:value
        });

    }
    onSubmit=(event)=>{
        if(this.state.name === '' )
        {
           alert('Ban chua nhap ten san pham!'); 
           event.preventDefault();
        }
        else if(this.state.price === '' )
        {
           alert('Ban chua nhap gia san pham!'); 
           event.preventDefault();
        }
        else if(this.state.quantity === '' )
        {
           alert('Ban chua nhap so luong san pham!'); 
           event.preventDefault();
        }
        else if(this.state.image === '' )
        {
           alert('Ban chua nhap URL hinh anh cua san pham!'); 
           event.preventDefault();
        }
        else{
            event.preventDefault();
            //console.log(this.state);
            this.props.onSubmityy(this.state);
            this.onCancle();
            this.oncloseForm();
        }

    }
    onCancle=()=>{
        this.setState({
            name: '',
            price:'',
            quantity:'',
            image:'',
            status: false
        });
    }

  render(){
      var {id}= this.state;
    return (
      <div className="panel panel-warning">
            <div className="panel-heading">
                  <h3 className="panel-title"> {id===''?'Thêm san pham ':'Chinh sua san pham'}
                        <span className="fa fa-times-circle text-right" onClick={this.oncloseForm}></span>
                  </h3>
            </div>
            <div className="panel-body">

                  <form onSubmit={this.onSubmit}>
                      <div className="form-group">
                          <label>Tên: </label>
                          <input type="text" className="form-control" name="name" value={this.state.name} onChange={this.onChange}/>
                      </div>
                      <div className="form-group">
                          <label>Giá: </label>
                          <input type="text" className="form-control" name="price" value={this.state.price} onChange={this.onChange}/>
                      </div>
                      <div className="form-group">
                          <label>So Luong: </label>
                          <input type="text" className="form-control" name="quantity" value={this.state.quantity} onChange={this.onChange}/>
                      </div>
                      <div className="form-group">
                          <label>URL Hình Ảnh: </label>
                          <input type="text" className="form-control" name="image" value={this.state.image} onChange={this.onChange}/>
                      </div>
                      <label>Trạng thái: </label>

                      <select className="form-control" name="status" value={this.state.status} onChange={this.onChange}>
                          <option value={true}>Kích Hoạt</option>
                          <option value={false}>Ẩn</option>
                      </select><br/>
                      <div className="text-center">
                            <button type="submit" className="btn btn-warning">
                                <span className="fa fa-plus mr-5" ></span> Lưu lại
                            </button>&nbsp;
                            <button type="button" className="btn btn-danger" onClick={this.onCancle}>
                                <span className="fa fa-close mr-5" ></span> Hủy bỏ
                            </button>&nbsp;
                      </div>

                  </form>

            </div>
      </div>

    );
  }

}

export default Form;
