import React, {Component} from 'react';
import '../App.css';

// import logger from 'logging-library';
// import FileViewer from 'react-file-viewer';
// import { CustomErrorComponent } from 'custom-error';
// const file = 'http://localhost:1337/uploads/Bang_Diem_intern_5e4e7e44f3.pdf'
// const type = 'pdf'
import { Viewer } from '@react-pdf-viewer/core';
import { defaultLayoutPlugin } from '@react-pdf-viewer/default-layout';
import '@react-pdf-viewer/core/lib/styles/index.css';
import '@react-pdf-viewer/default-layout/lib/styles/index.css';
const defaultLayoutPluginInstance = defaultLayoutPlugin();
//pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.min.js`;
class Item extends Component {
  onUpdateStatus=()=>{
   // console.log(this.props.task.id);
   this.props.onUpdateStatuszz(this.props.task.id)
  }
  onDelete=()=>{
    this.props.onDeletezz(this.props.task.id)
  }
  onUpdate=()=>{
    this.props.onUpdatezz(this.props.task.id)
  }
  render(){
    var {task,index} = this.props;
    return (
            <tr>
                  <td className="text-center cgiua">{index+1}</td>
                  <td>{task.name}</td>

                  <td className="text-center cgiua"><div>{task.price} vnđ</div></td>
                  <td className="text-center cgiua">{task.quantity}</td>
                  <td className="text-center cgiua"><Worker workerUrl="https://unpkg.com/pdfjs-dist@2.6.347/build/pdf.worker.js">            
            <div
                style={{
                    height: '720px',
                    width: '640px',
                }}
            >
                <Viewer
                    fileUrl="http://localhost:1337/uploads/Bang_Diem_intern_5e4e7e44f3.pdf"
                    plugins={[
                        defaultLayoutPluginInstance,
                    ]}
                />
            </div>
        </Worker>
    </td>
                  <td className="text-center cgiua">
                  <span className={task.status=== true ?'label label-success':'label label-danger'} onClick={this.onUpdateStatus}>{task.status === true ?'Kich hoat':'An'}</span>
                  </td>
                  <td className="text-center cgiua">     
                      <button type="button" className="btn btn-warning mlt-3-5" onClick={this.onUpdate}>
                          <i className="fa fa-pencil mr-5"></i>
                          Sửa
                        </button>&nbsp;
                        <button type="button" className="btn btn-danger" onClick={this.onDelete}>
                          <i className="fa fa-trash mr-5"></i>
                          Xóa
                        </button>
                  </td>
              </tr>

    );
  }

}

export default Item;
