import React, {Component} from 'react';
import Form from './components/Form';
import Search_sort from './components/Search_sort';
import List from './components/List';
class App extends Component {
  constructor(props){
    super(props);
    this.state={
      tasks: [],//id: unique, name, price, status
      displayform: false,
      taskEditing: null,
      filter: {
        name:'',
        price:'',
        status: -1
      },
      keyword: '',
      sort :{
        by:'',
        value: 1
      }
    }
    this.onGenerateData = this.onGenerateData.bind(this);
    this.onDisForm=this.onDisForm.bind(this);
  }
  UNSAFE_componentWillMount(){
    if(localStorage && localStorage.getItem('tasks')){
      var tasks = JSON.parse(localStorage.getItem('tasks'));
      this.setState({
        tasks: tasks
      });
    }
  }
  onGenerateData(){
    var tasks =[
      {
        id: this.generateID(),
        name: 'hoc reacjs',
        image: 'https://kenh14cdn.com/thumb_w/660/2020/7/17/brvn-15950048783381206275371.jpg',
        price:'10',
        status: false//an 
      },
      {
        id: this.generateID(),
        name: 'hoc reacjs2',
        image: 'https://ttol.vietnamnetjsc.vn/images/2018/05/25/13/40/net-cuoi-be-gai-9-1527053440039156820618.jpg',
        price:'20',
        status: false//an
      },
      {
        id: this.generateID(),
        name: 'hoc reacjs3',
        image: 'https://scr.vn/wp-content/uploads/2020/08/H%C3%8CNH-%E1%BA%A2NH-M%E1%BA%A0NG-%C4%90%E1%BA%B8P.jpg',
        price: '30',
        status: true//kich hoat
      }
    ];
    this.setState({
      tasks: tasks
    });
    localStorage.setItem('tasks',JSON.stringify(tasks));
  }

  s4(){
    return Math.floor((1+Math.random())*0x10000).toString(16).substring(1);
  }
  generateID(){
    return this.s4() + this.s4()+ '-' + this.s4() + '-' + this.s4() + '-' + this.s4() + '-' + this.s4() + '-' + this.s4() + '-' + this.s4() + '-' + this.s4();
  }
  onDisForm=()=>{//them task
    if(this.state.displayform && this.state.taskEditing !== null)
    {
      this.setState({

        // displayform: true,
        taskEditing:null

      });
    }
    else{
      this.setState({

        displayform: !this.state.displayform,
        taskEditing:null

      });
    }

  }
  oncloseForm=()=>{
    this.setState({
      displayform: false
    });
  }
  onshowForm=()=>{
    this.setState({
      displayform: true
    });
  }
  onSubmit=(data)=>{
    // var task = {
    //   id: this.generateID(),
    //   name: data.name
    // }
    var {tasks} = this.state; //tasks=this.state.tasks
    if(data.id === ''){//Them san pham
      data.id=this.generateID();
      tasks.push(data);
    }
    else{//Chinh sua san pham
      var index = this.findIndex(data.id);
      tasks[index]=data;
    }
    this.setState({
      tasks: tasks,
      taskEditing: null
    });
    localStorage.setItem('tasks',JSON.stringify(tasks));
  }
  onUpdateStatus=(id)=>{
    var {tasks} = this.state;
    var index = this.findIndex(id);
    //console.log(index);
    if(index!==-1){
      tasks[index].status = !tasks[index].status
      this.setState({
        tasks: tasks
      });
      localStorage.setItem('tasks',JSON.stringify(tasks));
    }

  }
  findIndex=(id)=>{
    var result =-1;
    var {tasks} = this.state;
    tasks.forEach((task,index)=>{
      if(task.id===id){
        //console.log(index);
        result = index;
      }
    });
    return result;
  }
  onDelete=(id)=>{
    var {tasks} = this.state;
    var index = this.findIndex(id);
    //console.log(index);
    if(index!==-1){
      tasks.splice(index,1);
      this.setState({
        tasks: tasks
      });
      localStorage.setItem('tasks',JSON.stringify(tasks));
      this.oncloseForm();
    }
  }
  onUpdate=(id)=>{
    var {tasks} = this.state;//{tasks} phai lay trung ten tasks trong json state #1
    var index = this.findIndex(id);
    var taskEditing = tasks[index];//or tasks.tasks[index] neu o #1 gan kieu var tasks= this.state
    this.setState({
      taskEditing: taskEditing
    });
    this.onshowForm();
  }
  onFilter=(filterName,filterPrice,filterStatus)=>{
    filterStatus = parseInt(filterStatus,10);
    this.setState({
      filter:{
        name: filterName.toLowerCase(),
        price: filterPrice,
        status: filterStatus
      }
    });
  }
  onSearch = (keyword)=>{
    this.setState({
      keyword:keyword
    });
  }
  onSort=async (sort)=>{
    await this.setState({
      sort:{
        by: sort.by,
        value: sort.value
      }
    })
  }
  render(){
    var {tasks,filter} = this.state;
    //console.log(filter);
    if(filter){
        if(filter.name){
          tasks = tasks.filter(task=>{
            return task.name.toLowerCase().indexOf(filter.name) !== -1;//khi kiem tra task.name.toLowerCase().indexOf(filter.name) ko co trong mang thi se tra ve gia tri -1 - cai task nao co return true thi tra ra 
          })
        }
        if(filter.price){
          tasks = tasks.filter(task=>{
            return task.price.toLowerCase().indexOf(filter.price) !== -1;//khi kiem tra ko co trong mang thi se tra ve gia tri -1
          })
        }
        if(filter.status || filter.status===0){//bo if o day cung dc
          tasks = tasks.filter(task=>{
            if(filter.status === -1){
              return tasks
            }
            else{
              return task.status === (filter.status === 1 ? true:false)
            }
          })
        }
      }
    if(this.state.keyword){
      tasks = tasks.filter(task=>{
        return task.name.toLowerCase().indexOf(this.state.keyword) !== -1;//khi kiem tra task.name.toLowerCase().indexOf(filter.name) ko co trong mang thi se tra ve gia tri -1 - cai task nao co return true thi tra ra 
      })
    }
    if(this.state.sort.by === 'name')
    {
      tasks.sort((a,b)=>{
        if(a.name>b.name){
          return this.state.sort.value
        }
        else if(a.name<b.name){
          return -this.state.sort.value
        }
        else{
          return 0;
        }
      })
    }else if(this.state.sort.by === 'status'){
      tasks.sort((a,b)=>{
        if(a.status>b.status){
          return -this.state.sort.value
        }
        else if(a.status<b.status){
          return this.state.sort.value
        }
        else{
          return 0;
        }
      })
    }

    console.log(this.state.sort)
    var disform = this.state.displayform ? <Form onSubmityy={this.onSubmit} oncloseForms = {this.oncloseForm} task={this.state.taskEditing}/> : '';
    return (

      <div className="container">
        <div className="text-center">
          <h1>Quan ly kho hang</h1><hr/>
        </div>    
        <div className="row">
          <div className={this.state.displayform ? "col-xs-4 col-sm-4 col-md-4 col-lg-4":''}>
            {disform}
          </div>

          <div className={this.state.displayform ? "col-xs-8 col-sm-8 col-md-8 col-lg-8":"col-xs-12 col-sm-12 col-md-12 col-lg-12"}>

            <button type="button" className="btn btn-primary" onClick={this.onDisForm} >
              <i className="fa fa-plus mr-5" ></i>
              Them san pham</button>
            <button type="button" className="btn btn-primary ml-5" onClick={this.onGenerateData}>
              Generate data</button>
              <Search_sort onSearchyy={this.onSearch} onSortyy={this.onSort}/>
              <List tasks={tasks} onUpdateStatusyy={this.onUpdateStatus} onDeleteyy={this.onDelete} onUpdateyy={this.onUpdate} onFilteryy={this.onFilter}/>

          </div>

        </div>
      </div>
    );
  }

}

export default App;
