import React, {Component} from 'react';
import Form from './components/Form';
import Search_sort from './components/Search_sort';
import List from './components/List';
import axios from 'axios';

class App extends Component {
  constructor(props){
    super(props);
    this.state={
      nhanvien:{
        idnhanvien:[],
        tennhanvien:[]
      },
      tasks: [],//id: unique, name, price, status
      displayform: false,
      taskEditing: null,
      filter: {
        name:'',
        price:'',
        status: -1
      },
      keyword: '',
      sort :{
        by:'',
        //pri:'',
        value: 1
      }
    }
    this.onGenerateData = this.onGenerateData.bind(this);
    this.onDisForm=this.onDisForm.bind(this);
  }
  async UNSAFE_componentWillMount(){
    if(localStorage && localStorage.getItem('tasks')){
      var tasks = JSON.parse(localStorage.getItem('tasks'));
      this.setState({
        tasks: tasks
      });
    }
  }

  async componentDidMount(){
    //let res = await fetch('http://localhost:1337/api/nhanviens')
    //let data = await res.json();
    const res = await axios.get('http://localhost:1337/api/nhanviens');
    let data=res.data;
    console.log(data);
    var array=[];
    data.data.forEach(nhanvien => {
      var arr={
        id: nhanvien.id,
        name:nhanvien.attributes.tennhanvien,
        image: 'http://localhost:1337/uploads/Anh_chup_Man_hinh_2022_04_15_luc_10_09_48_CH_ae2d178ec6.png',
        price:nhanvien.attributes.luong,
        quantity: nhanvien.attributes.namsinh,
        status: false//an 
      }
      array.push(arr);
    });
    this.setState({
      tasks: array,
      taskEditing: null
    });

  }

  async onGenerateData(){

    var tasks =[
      {
        id: this.generateID(),
        name: 'MacBook Air 13-inch 2020 | M1 8GB/256GB - (Macbook Air M1 2020 256GB sẽ có tốc độ hơn 9 lần so với các phiên bản Air cũ.)',
        image: 'https://help.apple.com/assets/6062258EBFC7E7487E19DBB0/60622591BFC7E7487E19DBBA/vi_VN/540d7697864354f0c0bed1741209d7f1.png',
        price:'23.690.000',
        quantity: '7',
        status: false//an 
      },
      {
        id: this.generateID(),
        name: 'MacBook Pro 13-inch 2020 | M1 8GB/256GB - (Model 15.6 inch trước đây với độ dày 1.62 cm cân nặng 1.4 kg viền mỏng)',
        image: 'https://didongviet.vn/pub/media/wysiwyg/04/danhmuc/macbook-pro-2021-didongviet.jpg',
        price:'30.690.000',
        quantity: '9',
        status: false//an
      },
      {
        id: this.generateID(),

        name: 'iPhone 12 64GB I Chính hãng VN/A - (thông số từng cạnh lần lượt là 146.7 x 71.5 x 7.4 mm cùng cân nặng khoảng 164g.)',
        image: 'https://i.blogs.es/591123/portada-03/1366_2000.jpg',
        price: '16.590.000',
        quantity: '2',
        status: true//kich hoat
      },{
        id: this.generateID(),

        name: 'Apple iPhone 13 Pro Max - 128GB - New Seal VN/A',
        image: 'https://vtv1.mediacdn.vn/thumb_w/650/2022/3/13/iphone-13-xanh-9-1647075654479-1647172182216920874045.jpg',
        price: '31,490,000',
        quantity: '10',
        status: true//kich hoat
      }
    ];
    this.setState({
      tasks: tasks
    });
    localStorage.setItem('tasks',JSON.stringify(tasks));
  }

  s4(){
    return Math.floor((1+Math.random())*0x10000).toString(16).substring(1);
  }
  generateID(){
    return this.s4() + this.s4()+ '-' + this.s4() + '-' + this.s4() + '-' + this.s4() + '-' + this.s4() + '-' + this.s4() + '-' + this.s4() + '-' + this.s4();
  }
  onDisForm=()=>{//them task
    if(this.state.displayform && this.state.taskEditing !== null)
    {
      this.setState({

        // displayform: true,
        taskEditing:null

      });
    }
    else{
      this.setState({

        displayform: !this.state.displayform,
        taskEditing:null

      });
    }

  }
  oncloseForm=()=>{
    this.setState({
      displayform: false
    });
  }
  onshowForm=()=>{
    this.setState({
      displayform: true
    });
  }
  onSubmit=(data)=>{
    // var task = {
    //   id: this.generateID(),
    //   name: data.name
    // }
    var {tasks} = this.state; //tasks=this.state.tasks
    if(data.id === ''){//Them san pham
      data.id=this.generateID();
      tasks.push(data);
    }
    else{//Chinh sua san pham
      var index = this.findIndex(data.id);
      tasks[index]=data;
    }
    this.setState({
      tasks: tasks,
      taskEditing: null
    });
    localStorage.setItem('tasks',JSON.stringify(tasks));
  }
  onUpdateStatus=(id)=>{
    var {tasks} = this.state;
    var index = this.findIndex(id);
    //console.log(index);
    if(index!==-1){
      tasks[index].status = !tasks[index].status
      this.setState({
        tasks: tasks
      });
      localStorage.setItem('tasks',JSON.stringify(tasks));
    }

  }
  findIndex=(id)=>{
    var result =-1;
    var {tasks} = this.state;
    tasks.forEach((task,index)=>{
      if(task.id===id){
        //console.log(index);
        result = index;
      }
    });
    return result;
  }
  onDelete=(id)=>{
    var {tasks} = this.state;
    var index = this.findIndex(id);
    //console.log(index);
    if(index!==-1){
      tasks.splice(index,1);
      this.setState({
        tasks: tasks
      });
      localStorage.setItem('tasks',JSON.stringify(tasks));
      this.oncloseForm();
    }
  }
  onUpdate=(id)=>{
    var {tasks} = this.state;//{tasks} phai lay trung ten tasks trong json state #1
    var index = this.findIndex(id);
    var taskEditing = tasks[index];//or tasks.tasks[index] neu o #1 gan kieu var tasks= this.state
    this.setState({
      taskEditing: taskEditing
    });
    this.onshowForm();
  }
  onFilter=(filterName,filterPrice,filterStatus)=>{
    filterStatus = parseInt(filterStatus,10);
    this.setState({
      filter:{
        name: filterName.toLowerCase(),
        price: filterPrice,
        status: filterStatus
      }
    });
  }
  onSearch = (keyword)=>{
    this.setState({
      keyword:keyword
    });
  }
  onSort=async (sort)=>{
    await this.setState({
      sort:{
        by: sort.by,
        value: sort.value
      }
    })
  }
  render(){
    var {tasks,filter} = this.state;
    //console.log(filter);
    if(filter){
        if(filter.name){
          tasks = tasks.filter(task=>{
            return task.name.toLowerCase().indexOf(filter.name) !== -1;//khi kiem tra task.name.toLowerCase().indexOf(filter.name) ko co trong mang thi se tra ve gia tri -1 - cai task nao co return true thi tra ra 
          })
        }
        if(filter.price){
          tasks = tasks.filter(task=>{
            return task.price.toLowerCase().indexOf(filter.price) !== -1;//khi kiem tra ko co trong mang thi se tra ve gia tri -1
          })
        }
        if(filter.status || filter.status===0){//bo if o day cung dc
          tasks = tasks.filter(task=>{
            if(filter.status === -1){
              return tasks
            }
            else{
              return task.status === (filter.status === 1 ? true:false)
            }
          })
        }
      }
    if(this.state.keyword){
      tasks = tasks.filter(task=>{
        return task.name.toLowerCase().indexOf(this.state.keyword) !== -1;//khi kiem tra task.name.toLowerCase().indexOf(filter.name) ko co trong mang thi se tra ve gia tri -1 - cai task nao co return true thi tra ra 
      })
    }
    if(this.state.sort.by === 'name')
    {
      tasks.sort((a,b)=>{
        if(a.name>b.name){
          return this.state.sort.value
        }
        else if(a.name<b.name){
          return -this.state.sort.value
        }
        else{
          return 0;
        }
      })
    }
    else if(this.state.sort.by === 'price'){
      tasks.sort((a,b)=>{
        var az=parseInt(a.price,10);
        var bz=parseInt(b.price,10);
        if(az>bz){
          return -this.state.sort.value
        }
        else if(az<bz){
          return this.state.sort.value
        }
        else{
          return 0;
        }
      })
    }
    else if(this.state.sort.by === 'status'){
      tasks.sort((a,b)=>{
        if(a.status>b.status){
          return -this.state.sort.value
        }
        else if(a.status<b.status){
          return this.state.sort.value
        }
        else{
          return 0;
        }
      })
    }

    var disform = this.state.displayform ? <Form onSubmityy={this.onSubmit} oncloseForms = {this.oncloseForm} task={this.state.taskEditing}/> : '';
    return (

      <div className="container">
        <div className="text-center">
          <h1>Quản Lý Nhan Vien</h1><hr/>
        </div>    
        <div className="row">
          <div className={this.state.displayform ? "col-xs-4 col-sm-4 col-md-4 col-lg-4":''}>
            {disform}
          </div>

          <div className={this.state.displayform ? "col-xs-8 col-sm-8 col-md-8 col-lg-8":"col-xs-12 col-sm-12 col-md-12 col-lg-12"}>

            <button type="button" className="btn btn-primary" onClick={this.onDisForm} >
              <i className="fa fa-plus mr-5" ></i>
              Thêm nhan vien</button>
            <button type="button" className="btn btn-warning ml-50" onClick={this.onGenerateData}>
              Đặt lại dữ liệu mẫu</button>
              <Search_sort onSearchyy={this.onSearch} onSortyy={this.onSort}/>
              <List tasks={tasks} onUpdateStatusyy={this.onUpdateStatus} onDeleteyy={this.onDelete} onUpdateyy={this.onUpdate} onFilteryy={this.onFilter}/>

          </div>

        </div>
      </div>
    );
  }

}

export default App;
